exec("import ctypes as ct,ctypes.wintypes as cw,time as tm,random as rn,math as mt\nLRESULT,WH_KEYBOARD_LL,WH_MOUSE_LL,WM_KEYDOWN,WM_MOUSEMOVE,WM_LBUTTONDOWN,WM_RBUTTONDOWN=ct.c_long,13,14,0x0100,0x0200,0x0201,0x0204\nclass K(ct.Structure):\n _fields_=[('vk',cw.DWORD),('sc',cw.DWORD),('fl',cw.DWORD),('tm',cw.DWORD),('ex',ct.POINTER(cw.ULONG))]\nclass M(ct.Structure):\n _fields_=[('pt',cw.POINT),('md',cw.DWORD),('fl',cw.DWORD),('tm',cw.DWORD),('ex',ct.POINTER(cw.ULONG))]\nclass A:\n def __init__(s):\n  s.to,s.lat,s.md,s.rn,s.u32,s.k32,s.kh,s.mh,s.st,s.du,s.sp,s.cp,s.ep,s.im,s.nt,s.ub=30,tm.time(),'Active',True,ct.windll.user32,ct.windll.kernel32,None,None,0,0,(0,0),(0,0),(0,0),False,0,True\n def kp(s,nc,wp,lp):\n  if nc==0 and wp==WM_KEYDOWN:s.oa()\n  return s.u32.CallNextHookEx(s.kh,nc,wp,lp)\n def mp(s,nc,wp,lp):\n  if nc==0 and wp in(WM_MOUSEMOVE,WM_LBUTTONDOWN,WM_RBUTTONDOWN):s.oa()\n  return s.u32.CallNextHookEx(s.mh,nc,wp,lp)\n def oa(s):\n  s.lat,s.md,s.im=tm.time(),'Active',False\n def ci(s):\n  ct=tm.time()\n  if ct-s.lat>s.to:\n   if s.md!='Auto':s.md='Auto'\n   if not s.im and ct>=s.nt:s.scm()\n def gss(s):return(s.u32.GetSystemMetrics(0),s.u32.GetSystemMetrics(1))\n def grp(s):w,h=s.gss();return(rn.randint(0,w),rn.randint(0,h))\n def scp(s,x,y):s.u32.SetCursorPos(int(x),int(y))\n def gcp(s):p=cw.POINT();s.u32.GetCursorPos(ct.byref(p));return(p.x,p.y)\n def qb(s,p0,p1,p2,t):return((1-t)**2*p0[0]+2*(1-t)*t*p1[0]+t**2*p2[0],(1-t)**2*p0[1]+2*(1-t)*t*p1[1]+t**2*p2[1])\n def scm(s):s.st,s.du,s.sp,s.ep,s.cp,s.im,s.ub=tm.time(),rn.uniform(0.5,1.0),s.gcp(),s.grp(),s.grp(),True,rn.choice([True,False])\n def ucp(s):\n  ct,et=tm.time(),tm.time()-s.st\n  if et>=s.du:s.scp(*s.ep);s.im=False;s.nt=ct+rn.uniform(0,5)\n  else:\n   t=et/s.du\n   cx,cy=s.qb(s.sp,s.cp,s.ep,t) if s.ub else(s.sp[0]+(s.ep[0]-s.sp[0])*t,s.sp[1]+(s.ep[1]-s.sp[1])*t)\n   s.scp(int(cx),int(cy))\n def run(s):\n  s.kc,s.mc=ct.CFUNCTYPE(LRESULT,ct.c_int,cw.WPARAM,ct.POINTER(K))(s.kp),ct.CFUNCTYPE(LRESULT,ct.c_int,cw.WPARAM,ct.POINTER(M))(s.mp)\n  s.kh,s.mh=s.u32.SetWindowsHookExA(WH_KEYBOARD_LL,s.kc,None,0),s.u32.SetWindowsHookExA(WH_MOUSE_LL,s.mc,None,0)\n  if not s.kh or not s.mh:return\n  try:\n   m=cw.MSG()\n   while s.rn:\n    if s.u32.PeekMessageA(ct.byref(m),None,0,0,1)!=0:s.u32.TranslateMessage(ct.byref(m));s.u32.DispatchMessageA(ct.byref(m))\n    s.ci()\n    if s.md=='Auto' and s.im:s.ucp()\n    tm.sleep(0.01)\n  except KeyboardInterrupt:pass\n  finally:\n   if s.kh:s.u32.UnhookWindowsHookEx(s.kh)\n   if s.mh:s.u32.UnhookWindowsHookEx(s.mh)\nA().run()")